import numpy as np

def check_broadcast_compatibility(input_data):
    array = np.array(input_data['array'], dtype=input_data['dtype'])
    shape = tuple(input_data['shape'])
    edge_cases = input_data['edge_cases']

    if array.shape != shape:
        return False

    if 'broadcasting' in edge_cases:
        try:
            np.empty(shape).reshape(array.shape)
        except ValueError:
            return False

    if 'memory_layout' in edge_cases:
        if array.flags['C_CONTIGUOUS']:
            return True

    return array.ndim == len(shape) and all(s1 == s2 or s1 == 1 or s2 == 1 for s1, s2 in zip(array.shape[::-1], shape[::-1]))

