import numpy as np

def is_broadcastable(input_data):
    array = np.array(input_data['array'], dtype=input_data['dtype'])
    shape = np.array(input_data['shape'])
    try:
        result_shape = np.broadcast(array, np.empty(shape)).shape
        return np.array_equal(result_shape, shape)
    except ValueError:
        return False

