def leap_year_edge_cases(input_data):
    date_string = input_data["date_string"]
    timezone = pytz.timezone(input_data["timezone"])
    operations = input_data["operations"]

    naive_dt = datetime.datetime.strptime(date_string, "%Y-%m-%d %H:%M:%S")
    aware_dt = timezone.localize(naive_dt)

    if "handle_ambiguous" in operations:
        aware_dt = timezone.normalize(aware_dt)

    if "add_days" in operations:
        aware_dt += datetime.timedelta(days=1)

    if "convert_timezone" in operations:
        aware_dt = aware_dt.astimezone(pytz.utc)

    return aware_dt.year % 4 == 0 and (aware_dt.year % 100 != 0 or aware_dt.year % 400 == 0)

