def circular_reference_handling(input_data):
    seen = {}
    def serialize(obj):
        if isinstance(obj, dict):
            if id(obj) in seen:
                return {"circular_reference": True}
            seen[id(obj)] = obj
            return {k: serialize(v) for k, v in obj.items()}
        if isinstance(obj, list):
            return [serialize(v) for v in obj]
        return obj

