import pytz

def business_day_calculations(input_data):
    date_string = input_data["date_string"]
    fmt = input_data["format"]
    target_timezone = input_data["target_timezone"]

    try:
        local_tz = pytz.timezone("EST")
        naive_dt = datetime.strptime(date_string, fmt)
        local_dt = local_tz.localize(naive_dt)
        target_tz = pytz.timezone(target_timezone)
        target_dt = local_dt.astimezone(target_tz)

        if target_dt.weekday() >= 5:
            return "weekend"
        return target_dt.strftime("%Y-%m-%d %H:%M:%S")
    except Exception:
        return "error"

