import requests

def timeout_and_retries(input_data):
    session = requests.Session()
    session.headers.update(input_data.get("headers", {}))
    for _ in range(3):
        try:
            response = session.request(input_data["method"], input_data["url"], json=input_data.get("data"), timeout=2)
