from datetime import datetime, timedelta

def check_dst(input_data):
    date_string = input_data["date_string"]
    timezone = pytz.timezone(input_data["timezone"])
    operations = input_data["operations"]

    naive_dt = datetime.strptime(date_string, "%Y-%m-%d %H:%M:%S")
    aware_dt = timezone.localize(naive_dt)

    for op in operations:
        if op == "add_days":
            aware_dt += timedelta(days=1)
        elif op == "convert_timezone":
            aware_dt = aware_dt.astimezone(pytz.utc)
        elif op == "handle_ambiguous":
            aware_dt = timezone.normalize(aware_dt)

    return aware_dt.dst() != timedelta(0)

