import requests

def timeout_and_retries(input_data):
    session = requests.Session()
    url = input_data.get("url")
    method = input_data.get("method").lower()
    data = input_data.get("data")
    headers = input_data.get("headers")

    if not url or method not in ['get', 'post']:
        return "error"

    retries = 3
    timeout = 2

    for _ in range(retries):
        try:
            response = session.request(method, url, json=data, headers=headers, timeout=timeout)
            if response.status_code == 200:
                return "success"
        except (requests.Timeout, requests.ConnectionError):
            continue
    return "failed"

input_data = {"url": "https://httpbin.org/post", "method": "POST", "data": {"key": "value"}, "headers": {"Content-Type": "application/json"}}
result = timeout_and_retries(input_data)
print(result)

