import datetime
import pytz

def date_parsing(input_data):
    try:
        local_tz = pytz.timezone(input_data['date_string'].split()[-1])
        naive_dt = datetime.datetime.strptime(input_data['date_string'], input_data['format'])
        aware_dt = local_tz.localize(naive_dt)
        target_tz = pytz.timezone(input_data['target_timezone'])
        target_dt = aware_dt.astimezone(target_tz)
        return target_dt.strftime("%Y-%m-%d %H:%M:%S")
    except Exception:
        return "error"

