import requests

def count_successful_requests(input_data):
    session = requests.Session()
    success_count = 0
    try:
        response = session.request(input_data.get("method", "GET"), input_data["url"], json=input_data.get("data"), headers=input_data.get("headers"))
        if response.status_code == 200:
            success_count += 1
    except (requests.RequestException, TypeError):
        return "error"
    finally:
        session.close()
    return success_count

